import React, { useRef, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
    const videoRef = useRef(null);
    const [stream, setStream] = useState(null); // To hold the media stream
    const [imageData, setImageData] = useState(''); // To hold the captured image data
    const navigate = useNavigate();

    const getVideo = () => {
        navigator.mediaDevices.getUserMedia({ video: { facingMode: "user" } })
        .then(stream => {
            videoRef.current.srcObject = stream;
            videoRef.current.play();
            setStream(stream);
        })
        .catch(err => {
            console.error("Error accessing the camera: ", err);
        });
    };

    const takePhoto = () => {
        const canvas = document.createElement('canvas');
        canvas.width = 640;
        canvas.height = 480;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
        const data = canvas.toDataURL('image/png');
        setImageData(data); // Store the captured image
    };

    const stopVideoStream = () => {
        if (stream) {
            stream.getTracks().forEach(track => track.stop());
            videoRef.current.srcObject = null;
        }
    };

    const handleLogin = () => {
        stopVideoStream(); // Stop the camera after taking the photo
        axios.post('http://localhost:5000/login', { image: imageData })
            .then(response => {
                navigate('/profile', { state: { user: response.data } });
            })
            .catch(error => {
                console.error('Login failed:', error);
                alert('Login failed');
            });
    };

    return (
        <div>
            <h1>Login</h1>
            <video ref={videoRef} style={{ width: '100%' }}></video>
            <div>
                <button onClick={getVideo}>Activate Camera</button>
                <button onClick={takePhoto}>Take Photo</button>
                <button onClick={handleLogin} disabled={!imageData}>Login</button>
            </div>
        </div>
    );
}

export default Login;
