import React, { useRef, useState } from 'react';
import axios from 'axios';

function App() {
  const videoRef = useRef(null);
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [photos, setPhotos] = useState([]);

  const getVideo = () => {
    navigator.mediaDevices
      .getUserMedia({ video: { facingMode: "user" } })
      .then(stream => {
        let video = videoRef.current;
        video.srcObject = stream;
        video.play();
      })
      .catch(err => {
        console.error("Error accessing the camera: ", err);
      });
  };

  const takePhoto = () => {
    const canvas = document.createElement('canvas');
    canvas.width = 640;
    canvas.height = 480;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
    const imageData = canvas.toDataURL('image/png');
    setPhotos(prevPhotos => [...prevPhotos, imageData]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (photos.length < 3) {
      alert('Please capture all three photos.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/register', {
        name,
        surname,
        photo1: photos[0],
        photo2: photos[1],
        photo3: photos[2]
      });
      alert('User registered: ' + response.data.message);
    } catch (error) {
      console.error('Registration failed:', error);
      alert('Registration failed');
    }
  };

  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={name} onChange={e => setName(e.target.value)} placeholder="Name" required />
        <input type="text" value={surname} onChange={e => setSurname(e.target.value)} placeholder="Surname" required />
        <video ref={videoRef} style={{ width: '100%' }}></video>
        <button type="button" onClick={getVideo}>Activate Camera</button>
        <button type="button" onClick={takePhoto} disabled={photos.length >= 3}>Capture Photo</button>
        <button type="submit">Register</button>
        {photos.map((photo, index) => (
          <img key={index} src={photo} alt={`Captured ${index + 1}`} style={{ width: '100px' }} />
        ))}
      </form>
    </div>
  );
}

export default App;
