import React, { useRef, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
    const videoRef = useRef(null);
    const [stream, setStream] = useState(null);
    const [imageData, setImageData] = useState('');
    const [cameraActive, setCameraActive] = useState(false);
    const navigate = useNavigate();

    const getVideo = () => {
        if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
            navigator.mediaDevices.getUserMedia({ video: true })
            .then(stream => {
                videoRef.current.srcObject = stream;
                videoRef.current.play();
                setStream(stream);
                setCameraActive(true);
            })
            .catch(err => {
                console.error("Error accessing the camera: ", err);
            });
        }
    };

    const takePhoto = () => {
        if (imageData) {
            if (window.confirm("Are you sure you want to take another photo? This will discard the current one.")) {
                capturePhoto();
            }
        } else {
            capturePhoto();
        }
    };

    const capturePhoto = () => {
        const canvas = document.createElement('canvas');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
        const newImageData = canvas.toDataURL('image/png');
        setImageData(newImageData);
    };

    const stopVideoStream = () => {
        if (stream) {
            stream.getTracks().forEach(track => track.stop());
            videoRef.current.srcObject = null;
            setCameraActive(false);
        }
    };

    const handleLogin = () => {
        stopVideoStream();
        axios.post('http://localhost:5000/login', { image: imageData })
            .then(response => {
                navigate('/profile', { state: { user: response.data } });
            })
            .catch(error => {
                console.error('Login failed:', error);
                alert('Login failed');
            });
    };

    return (
            <div className="container">
                <h1>Login</h1>
                <video ref={videoRef}></video>
                <button onClick={cameraActive ? takePhoto : getVideo}>
                    {cameraActive ? 'Take Photo' : 'Activate Camera'}
                </button>
                <button onClick={handleLogin} disabled={!imageData}>Login</button>
                {imageData && <img src={imageData} alt="Captured" />}
            </div>
        );
    }

    export default Login;
