import React, { useRef, useState } from 'react';
import axios from 'axios';

function App() {
  const videoRef = useRef(null);
  const [image, setImage] = useState('');
  const [error, setError] = useState('');


  const getVideo = () => {
    navigator.mediaDevices.
    getUserMedia({video: { facingMode: "user" } })
    .then(stream => {
      let video = videoRef.current;
      video.srcObject = stream;
      video.play();
    })
    .catch(err => {
      console.error("Error accessing the camera: ", err);
      setError("Error accessing the camera: " + err.message);
    });
  };

  const takePhoto = () => {
    const width = 640;
    const height = 480;
    let video = videoRef.current;
    let canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    let ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, width, height);
    const imageData = canvas.toDataURL('image/png');
    setImage(imageData);
  };

  const uploadPhoto = () => {
    if (!image) {
      setError("No image captured.");
      alert("No image captured.");
      return;
    }
    axios.post('http://loalhost:5000/upload', { image:image })
    .then(response => alert('Upload successful ' + response.data.path))
    .catch(err => {
      console.error('Upload failed:', err);
      setError('Upload failed: ' + (err.response ? err.response.data.error : err.message));
    });
  };

  return (
    <div>
      <h1>Camera Feed</h1>
      <video ref={videoRef}></video>
      <br />
      <button onClick={getVideo}>Activate Camera</button>
      <button onClick={takePhoto}>Capture Photo</button>
      <button onClick={uploadPhoto}>Upload photo</button>
      <br />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {image && <img src={image} alt="Captured" style={{ width: '30%' }} />}

    </div>
  );
}

export default App;
