import React, { useRef, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

function Registration() {
    const navigate = useNavigate(); // Initialize navigate function
    const videoRef = useRef(null);
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [photos, setPhotos] = useState([]);
    const [stream, setStream] = useState(null); // To hold the media stream

    const getVideo = () => {
        navigator.mediaDevices.getUserMedia({ video: { facingMode: "user" } })
        .then(stream => {
            videoRef.current.srcObject = stream;
            videoRef.current.play();
            setStream(stream); // Save the stream for later
        })
        .catch(err => {
            console.error("Error accessing the camera: ", err);
        });
    };

    const takePhoto = () => {
        if (photos.length >= 3) {
            return; // Do not take more than 3 photos
        }
        const canvas = document.createElement('canvas');
        canvas.width = 640;
        canvas.height = 480;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
        const imageData = canvas.toDataURL('image/png');
        setPhotos(prevPhotos => [...prevPhotos, imageData]);
        if (photos.length === 2) {
            stopVideoStream(); // Stop the video stream once 3 photos are taken
        }
    };

    const stopVideoStream = () => {
        if (stream) {
            stream.getTracks().forEach(track => track.stop());
            videoRef.current.srcObject = null; // Clear the video element
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (photos.length < 3) {
            alert('Please capture all three photos.');
            return;
        }
        try {
            const response = await axios.post('http://localhost:5000/register', {
                name,
                surname,
                photo1: photos[0],
                photo2: photos[1],
                photo3: photos[2]
            });
            alert('User registered successfully: ' + response.data.message);
            navigate('/'); // Navigate to the main page after successful registration
        } catch (error) {
            console.error('Registration failed:', error);
            alert('Registration failed');
        }
    };

    return (
        <div>
            <h1>User Registration</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" value={name} onChange={e => setName(e.target.value)} placeholder="Name" required />
                <input type="text" value={surname} onChange={e => setSurname(e.target.value)} placeholder="Surname" required />
                <div>
                    <video ref={videoRef} style={{ width: '100%' }}></video>
                    <button type="button" onClick={getVideo}>Activate Camera</button>
                    <button type="button" onClick={takePhoto} disabled={photos.length >= 3}>Capture Photo</button>
                </div>
                {photos.map((photo, index) => (
                    <img key={index} src={photo} alt={`Captured ${index}`} style={{ width: '100px', height: '100px' }} />
                ))}
                <button type="submit">Register</button>
                <button type="button" onClick={() => navigate('/')}>Go Back</button> {/* Go Back Button */}
            </form>
        </div>
    );
}

export default Registration;
