import React, { useRef, useState } from 'react';
import axios from 'axios'

function App() {
  const videoRef = useRef(null);
  const [image, setImage] = useState('');

  const getVideo = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
    .then(stream => {
      let video = videoRef.current;
      video.srcObject = stream;
      video.play();
    })
    .catch(err => {
      console.error("Error accessing the camera: ", err);
    });
  };

  const takePhoto = () => {
    const width = 640;
    const height = 480;
    let video = videoRef.current;
    let canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    let ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, width, height);
    const imageData = canvas .toDataURL('image/png');
    setImage(imageData);
  };

  const uploadImage = () => {
    axios.post('/upload', { image })
    .then(response => alert('Upload successful'))
    .catch(err => alert('Upload failed: ' + err.message));
  };

  return (
    <div>
      <h1>Biometric Photo Capture</h1>
      <video ref={VideoRef}></video>
      <button onClick={getVideo}>Activate Camera</button>
      <button onClick={takePhoto}>Capure Photo</button>
      <button onClick={uploadImage}>Upload photo</button>

      {image && <img src={image} alt="captured"/>}

    </div>
  );
}
