import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

function Register() {
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [photo1, setPhoto1] = useState(null);
    const [photo2, setPhoto2] = useState(null);
    const [photo3, setPhoto3] = useState(null);
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    const handleFileChange = (e, setPhoto) => {
        setPhoto(e.target.files[0]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Simple validation
        let validationErrors = {};
        if (!name) validationErrors.name = "Name is required";
        if (!surname) validationErrors.surname = "Surname is required";
        if (!photo1 || !photo2 || !photo3) validationErrors.photos = "All three photos are required";

        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }

        const formData = new FormData();
        formData.append('name', name);
        formData.append('surname', surname);
        formData.append('photo1', photo1);
        formData.append('photo2', photo2);
        formData.append('photo3', photo3);

        axios.post('http://localhost:5000/register', formData)
            .then(response => {
                alert(response.data.message);
                navigate('/login');
            })
            .catch(error => {
                console.error('Registration failed:', error);
                alert('Registration failed');
            });
    };

    return (
        <div className="container">
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
                </label>
                {errors.name && <p className="error">{errors.name}</p>}

                <label>
                    Surname:
                    <input type="text" value={surname} onChange={(e) => setSurname(e.target.value)} />
                </label>
                {errors.surname && <p className="error">{errors.surname}</p>}

                <label>
                    Photo 1:
                    <input type="file" onChange={(e) => handleFileChange(e, setPhoto1)} />
                </label>
                <label>
                    Photo 2:
                    <input type="file" onChange={(e) => handleFileChange(e, setPhoto2)} />
                </label>
                <label>
                    Photo 3:
                    <input type="file" onChange={(e) => handleFileChange(e, setPhoto3)} />
                </label>
                {errors.photos && <p className="error">{errors.photos}</p>}

                <button type="submit">Register</button>
            </form>
            <div style={{ marginTop: '20px' }}>
                <Link to="/login">Already have an account? Login</Link>
            </div>
        </div>
    );
}

export default Register;
